import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { taskAPI } from '../services/api';

const AddTask = ({ user }) => {
  const navigate = useNavigate();
  const [taskData, setTaskData] = useState({
    title: '',
    description: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    setTaskData({
      ...taskData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!taskData.title.trim() || !taskData.description.trim()) {
      setError('Please fill in all fields');
      return;
    }

    setLoading(true);
    setError('');

    try {
      await taskAPI.createTask(taskData);
      setSuccess(true);
      setTaskData({ title: '', description: '' });
      
      setTimeout(() => {
        setSuccess(false);
      }, 3000);
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to create task');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-task-container">
      <div className="page-header">
        <button className="back-btn" onClick={() => navigate('/welcome')}>
          <i className="fas fa-arrow-left"></i>
          Back to Dashboard
        </button>
        <h1>
          <i className="fas fa-plus-circle"></i>
          Add New Task
        </h1>
      </div>

      <div className="add-task-card">
        <div className="card-header">
          <div className="user-info">
            <span>Creating task for: <strong>{user?.username}</strong></span>
          </div>
        </div>

        {success && (
          <div className="success-message">
            <i className="fas fa-check-circle"></i>
            Task created successfully!
          </div>
        )}

        {error && (
          <div className="error-message">
            <i className="fas fa-exclamation-circle"></i>
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="task-form">
          <div className="form-group">
            <label htmlFor="title">
              <i className="fas fa-heading"></i>
              Task Title
            </label>
            <input
              type="text"
              id="title"
              name="title"
              placeholder="Enter task title..."
              value={taskData.title}
              onChange={handleChange}
              maxLength="100"
              required
            />
            <small>{taskData.title.length}/100 characters</small>
          </div>

          <div className="form-group">
            <label htmlFor="description">
              <i className="fas fa-align-left"></i>
              Task Description
            </label>
            <textarea
              id="description"
              name="description"
              placeholder="Describe your task in detail..."
              value={taskData.description}
              onChange={handleChange}
              maxLength="500"
              rows="6"
              required
            ></textarea>
            <small>{taskData.description.length}/500 characters</small>
          </div>

          <div className="form-actions">
            <button 
              type="button" 
              className="btn secondary"
              onClick={() => navigate('/welcome')}
              disabled={loading}
            >
              <i className="fas fa-times"></i>
              Cancel
            </button>
            <button 
              type="submit" 
              className="btn primary"
              disabled={loading}
            >
              {loading ? (
                <div className="button-loading">
                  <div className="spinner"></div>
                  Creating...
                </div>
              ) : (
                <>
                  <i className="fas fa-plus"></i>
                  Add Task
                </>
              )}
            </button>
          </div>
        </form>
      </div>

      <div className="tips-section">
        <h3>
          <i className="fas fa-lightbulb"></i>
          Tips for Better Task Management
        </h3>
        <div className="tips-grid">
          <div className="tip-item">
            <i className="fas fa-target"></i>
            <p><strong>Be Specific:</strong> Write clear and specific task titles</p>
          </div>
          <div className="tip-item">
            <i className="fas fa-list-ol"></i>
            <p><strong>Break It Down:</strong> Divide large tasks into smaller ones</p>
          </div>
          <div className="tip-item">
            <i className="fas fa-clock"></i>
            <p><strong>Set Priorities:</strong> Focus on important tasks first</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddTask;
